<template>

    <div class="pdf">
        <!-- <button class="btn" @click="downloadPDF">Download PDF</button> -->
        <input type="text" v-model="name" placeholder="Wat is je naam?" />
        <button type="button" @click="downloadPDF">Download Certificaat</button>

    </div>

</template>

<script>

    import jsPDF from 'jspdf'
    export default {

        name: 'pdf',
        data() {
            return {

            }
        },

        methods: {
            downloadPDF() {


                //initialize a new instance of jspdf
                var pdf = new jsPDF('landscape'); 


                var imgData = 'data:image/png;base64,'

                //adds image to pdf
                pdf.addImage(imgData, 'png', 110, 58, 150, 150);


                pdf.setFontSize(22);
                pdf.text("Certificaat van geweldigheid", 100, 25);

                pdf.setFontSize(12);
                pdf.text("Uitgereikt aan", 125, 60);

       
                pdf.setFontSize(26);
                //whatever text will be in the pdf (this.name refers to the v-model input)
                pdf.text(this.name, 100, 90);

                //save the pdf
                pdf.save('Bloedprik.pdf');
            }

        }
    }

</script>